ar(--bg-color-sub);\n  display: flex;\n  align-items: center;\n  justify-content: center;\n}\n\n.button[_ngcontent-%COMP%]   .svg[_ngcontent-%COMP%] {\n  width: 20px;\n  stroke: var(--main-color);\n}\n\n.button[_ngcontent-%COMP%]:hover {\n  background: var(--bg-color);\n}\n\n.button[_ngcontent-%COMP%]:hover   .button__text[_ngcontent-%COMP%] {\n  color: transparent;\n}\n\n.button[_ngcontent-%COMP%]:hover   .button__icon[_ngcontent-%COMP%] {\n  width: 148px;\n  transform: translateX(0);\n}\n\n.button[_ngcontent-%COMP%]:active {\n  transform: translate(3px, 3px);\n  box-shadow: 0px 0px var(--main-color);\n}\n\n.btn[_ngcontent-%COMP%] {\n  background-color: transparent;\n  position: relative;\n  border: none;\n}\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n\n.icon[_ngcontent-%COMP%] {\n  transform: scale(1.2);\n  transition: 0.2s linear;\n}\n\n.btn[_ngcontent-%COMP%]:hover    > .icon[_ngcontent-%COMP%] {\n  transform: scale(1.5);\n}\n\n.btn[_ngcontent-%COMP%]:hover    > .icon[_ngcontent-%COMP%]   path[_ngcontent-%COMP%] {\n  fill: rgb(168, 7, 7);\n}\n\n.btn[_ngcontent-%COMP%]:hover::after {\n  visibility: visible;\n  opacity: 1;\n  top: -160%;\n}\n\n.button1[_ngcontent-%COMP%] {\n  display: flex;\n  justify-content: center;\n  align-items: center;\n  padding: 6px 12px;\n  gap: 4px;\n  height: 32px;\n  width: 81px;\n  border: none;\n  background: #1b1b1cd0;\n  border-radius: 20px;\n  cursor: pointer;\n}\n\n.lable[_ngcontent-%COMP%] {\n  line-height: 22px;\n  font-size: 17px;\n  color: #de8a2a;\n  font-family: sans-serif;\n  letter-spacing: 1px;\n}\n\n.button1[_ngcontent-%COMP%]:hover {\n  background: #1b1b1cea;\n}\n\n.button1[_ngcontent-%COMP%]:hover   .svg-icon[_ngcontent-%COMP%] {\n  animation: _ngcontent-%COMP%_rotate 1.3s linear infinite;\n}\n\n@keyframes _ngcontent-%COMP%_rotate {\n  0% {\n    transform: rotate(0deg);\n  }\n\n  25% {\n    transform: rotate(10deg);\n  }\n\n  50% {\n    transform: rotate(0deg);\n  }\n\n  75% {\n    transform: rotate(-10deg);\n  }\n\n  