import { Component, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  Validators,
} from '@angular/forms';
import { ActivatedRoute } from '@angular/router';
import { ProductsService } from 'src/app/services/Products/products.service';
@Component({
  selector: 'app-update-product',
  templateUrl: './update-product.component.html',
  styleUrls: ['./update-product.component.css'],
})
export class UpdateProductComponent implements OnInit {
  ID: any;
 // AllCategories: any[] | any = [];
  //uAllSubCategories: any[] | any = [];
  uParentCategories: any[] = [];
  uSubCategories: any[] = [];
  uselectedParentId: any;
  uselectedSubItem: any;
  uproductFromDb: any[] | any = [];
  colors: [] | any = [];
  usizes: [] | any = [];
  usubItemsDisabled = true;
  uquantity: any;
  ucolor: any;
  usize: any;
  uname: any;
  uproductInfo: { color: any; size: any; quantity: any }[] = [];
  uproductImages: any[] = [];
  uproductCategories: any[] = [];

  constructor(
    private ProductService: ProductsService,
    private build: FormBuilder,
   // private route:ActivatedRoute
  ) {
    // this.getAllCatergories();
    //this.ID = route.snapshot.params["id"];
    this.validFrm.get('PSubCat')?.disable();
  }

  ngOnInit(): void {
    //this.ugetAllCatergories();

    this.ProductService.getProductById(this.ID).subscribe({
      next: (data: any) => {
        this.uproductFromDb = data;
        this.uproductInfo = data.productInfo;
        console.log(this.uproductInfo);
        console.log(data);
      },
      error: (err) => {
        err;
      },
    });
  }

  //getallcategories
  // ugetAllCatergories() {
  //   this.ProductService.getAllCategories().subscribe((res) => {
  //     this.AllCategories = res;
  //     console.log(this.AllCategories);
  //     for (var i = 0; i < this.AllCategories.length; i++) {
  //       // console.log('loop');

  //       if (this.AllCategories[i].parentCategoryId != null) {
  //         this.uAllSubCategories.push(this.AllCategories[i]);
  //       } else {
  //         this.uParentCategories.push(this.AllCategories[i]);
  //       }
  //     }
  //     console.log(this.uAllSubCategories);
  //     console.log(this.uParentCategories);
  //   });
  // }
  //Add product info

  duplicateError: boolean = false;

  uAddProductInfo() {
    // if (this.productInfo.length >= 3) {
    //   console.log('Maximum number of rows 3');
    //   return;
    // }
    if (!this.ucolor || !this.usize || !this.uquantity) {
      console.log(' Fill all fields.');
      return;
    }
    const isDuplicate = this.uproductInfo.some(
      (info) => info.color === this.ucolor && info.size === this.usize
    );

    if (isDuplicate) {
      console.log('Duplicate Data');
      console.log(isDuplicate);
      this.duplicateError = true;
      return;
    }

    let Infos: any = {
      color: this.ucolor,
      size: this.usize,
      quantity: this.uquantity,
    };
    console.log(Infos);
    this.uproductInfo.push(Infos);
    console.log(this.uproductInfo);
    this.isSuccess = true;
    this.duplicateError = false;
  }

  udeleteProductInfo(index: number) {
    this.uproductInfo.splice(index, 1);

    console.log(this.uproductInfo);
  }
  //add product
  isSuccess: boolean = false;

  uaddProduct(name: any, description: any, price: any, discount: any, id: any) {
    let AddProduct = {
      id: '52013B52-A1E4-44F1-85E7-426482C144B5',
      name: name,
      description: description,
      price: price,
      discount: discount,
      productImages: [{}],
      productCategories: [{ id }],
      productInfo: this.uproductInfo,
    };
    if (this.validFrm.valid) console.log(AddProduct);
    this.ProductService.updateProductBy(AddProduct).subscribe({
      next: () => {
        console.log('Updated Success');
        console.log(AddProduct);
        this.isSuccess = true;
      },
      error: (err) => {
        err;
        console.log('Fail');
      },
    });
  }

  //Select Items
   uonItemSelected() {
  //   console.log(this.uselectedParentId);

  //   if (this.uselectedParentId != null) {
  //     this.uSubCategories = this.uAllSubCategories.filter(
  //       (subItem: { parentCategoryId: String }) =>
  //         subItem.parentCategoryId === this.uselectedParentId
  //     );
  //     console.log(this.uSubCategories);
  //     this.validFrm.get('PSubCat')?.enable();
  //   }
   }

  //validation

  validFrm = new FormGroup({
    uName: new FormControl('', [Validators.required, Validators.minLength(3)]),
    uDescription: new FormControl('', [
      Validators.required,
      Validators.maxLength(500),
    ]),
    uPrice: new FormControl('', [Validators.required, Validators.min(0)]),

    uDiscount: new FormControl('', [
      Validators.required,
      //Validators.pattern(/^\d{11}$/),
      Validators.min(0),
      Validators.max(1),
    ]),
    // PIMGS: new FormControl('', Validators.required),
    Pcolor: new FormControl('', Validators.required),
    uInfo: new FormControl('', [
      Validators.required,
      Validators.maxLength(500),
    ]),
    uSize: new FormControl('', [Validators.required]),
    uQuantity: new FormControl('', [Validators.required]),
    uCat: new FormControl('', [Validators.required]),
    uSubCat: new FormControl({ disabled: true }, [Validators.required]),
  });

  keyPressNumbers(event: any, len: number) {
    let inp = String.fromCharCode(event.keyCode);
    /[0-9]/.test(inp) ? true : event.preventDefault();

    let inp1 = event.target.value;
    inp1.length < len ? true : event.preventDefault();
  }

  keyPressMinLength(event: any, len: number) {
    let inp = event.target.value;
    inp.length < len ? true : event.preventDefault();
  }

  Sizes = ['S', 'M', 'L', 'XL', 'XXL', 'XXXL'];
  Colors = [
    'Red',
    'Blue',
    'Green',
    'Yellow',
    'Orange',
    'Purple',
    'Pink',
    'Black',
    'White',
    'Gray',
    'Brown',
  ];
}
<div class="container rounded bg-white mt-5 mb-5 border">
  <div class="row">
    <h1 class="d-flex justify-content-center">Update Product</h1>
    <hr />
    <form
      [formGroup]="validFrm"
      (ngSubmit)="
        uaddProduct(
          uName.value,
          uDescription.value,
          uPrice.value,
          uDiscount.value,
          uSubCat.value
        )
      "
    >
      <div class="row g-3">
        <div class="col-md-3 col-sm-12">
          <label for="uName">Name</label>
          <input
            #uName
            type="text"
            class="form-control"
            id="uName"
            formControlName="uName"
            placeholder="Product Name"
            [(ngModel)]="uproductFromDb.name"
            required
          />
          <div
            class="form-control-feedback"
            *ngIf="
              validFrm.get('uName')?.hasError('required') &&
              (validFrm.get('uName')?.dirty || validFrm.get('uName')?.touched)
            "
          >
            <p
              *ngIf="validFrm?.get('uName')?.errors?.['required']"
              class="text-danger"
            >
              this field is required
            </p>
            <p
              *ngIf="validFrm?.get('uName')?.errors?.['minlength']"
              class="text-danger"
            >
              Product name must be more than 3 letters
            </p>
          </div>
        </div>
        <div class="col-md-3 col-sm-12">
          <label for="uDescription">Description</label>
          <input
            [(ngModel)]="uproductFromDb.description"
            #uDescription
            type="text"
            class="form-control w-100"
            id="uDescription"
            formControlName="uDescription"
            placeholder="Description"
            required
          />
          <div
            class="form-control-feedback"
            *ngIf="
              validFrm.get('uDescription')?.errors &&
              (validFrm.get('uDescription')?.dirty ||
                validFrm.get('uDescription')?.touched)
            "
          >
            <p
              *ngIf="validFrm?.get('uDescription')?.errors?.['required']"
              class="text-danger"
            >
              this field is required
            </p>
          </div>
        </div>
        <div class="col-md-3 col-sm-12">
          <label for="uPrice">Price</label>
          <input
            [(ngModel)]="uproductFromDb.price"
            #uPrice
            type="number"
            class="form-control w-100"
            id="uPrice"
            name="uPrice"
            formControlName="uPrice"
            min="1"
          />
          <div
            class="form-control-feedback"
            *ngIf="
              validFrm.get('uPrice')?.errors &&
              (validFrm.get('uPrice')?.dirty || validFrm.get('uPrice')?.touched)
            "
          >
            <p
              *ngIf="validFrm?.get('uPrice')?.errors?.['required']"
              class="text-danger"
            >
              this field is required
            </p>
          </div>
        </div>
        <div class="col-md-3 col-sm-12">
          <label for="uDiscount">Discount</label>
          <input
            [(ngModel)]="uproductFromDb.discount"
            #uDiscount
            type="number"
            class="form-control w-100"
            id="uDiscount"
            name="uDiscount"
            formControlName="uDiscount"
            min="0"
            max="1"
            step="0.1"
          />
          <div
            class="form-control-feedback"
            *ngIf="
              validFrm.get('uDiscount')?.errors &&
              (validFrm.get('uDiscount')?.dirty ||
                validFrm.get('uDiscount')?.touched)
            "
          >
            <p
              *ngIf="validFrm?.get('uDiscount')?.errors?.['required']"
              class="text-danger"
            >
              this field is required
            </p>
          </div>
        </div>

        <div class="w-100">
          <div class="row border p-3 m-3 mb-6">
            <div class="col-sm-4">
              <div>
                <label for="uCat">Categories</label>
                <div class="pb-2">
                  <select
                    [(ngModel)]="uselectedParentId"
                    (ngModelChange)="uonItemSelected()"
                    #uCat
                    name="uCat"
                    id="uCat"
                    class="form-select"
                    aria-label="Default select example"
                    formControlName="uCat"
                  >
                    <option
                      *ngFor="let item of uParentCategories"
                      [value]="item.id"
                    >
                      {{ item.name }}
                    </option>
                  </select>
                  <div
                    class="form-control-feedback"
                    *ngIf="
                      validFrm.get('uCat')?.errors &&
                      (validFrm.get('uCat')?.dirty ||
                        validFrm.get('uCat')?.touched)
                    "
                  >
                    <p
                      *ngIf="validFrm?.get('uCat')?.errors?.['required']"
                      class="text-danger"
                    >
                      this field is required
                    </p>
                  </div>
                </div>

                <select
                  [(ngModel)]="uselectedSubItem"
                  #uSubCat
                  name="uSubCat"
                  id="uSubCat"
                  class="form-select"
                  aria-label="Default select example"
                  formControlName="uSubCat"
                >
                  <option
                    *ngFor="let subItem of uSubCategories"
                    [value]="subItem.id"
                  >
                    {{ subItem.name }}
                  </option>
                </select>
                <div
                  class="form-control-feedback"
                  *ngIf="
                    validFrm.get('uSubCat')?.errors &&
                    (validFrm.get('uSubCat')?.dirty ||
                      validFrm.get('uSubCat')?.touched)
                  "
                >
                  <p
                    *ngIf="validFrm?.get('uSubCat')?.errors?.['required']"
                    class="text-danger"
                  >
                    this field is required
                  </p>
                </div>
              </div>
              <div class="col-md-4 col-sm-12 pt-2">
                <label for="uIMGS">Image</label>

                <input type="file" class="form-control" />
                <img src="" alt="" />
                <div
                  class="form-control-feedback"
                  *ngIf="
                    validFrm.get('uIMGS')?.errors &&
                    (validFrm.get('uIMGS')?.dirty ||
                      validFrm.get('uIMGS')?.touched)
                  "
                >
                  <p
                    *ngIf="validFrm?.get('uIMGS')?.errors?.['required']"
                    class="text-danger"
                  >
                    this field is required
                  </p>
                </div>
              </div>
            </div>
            <div class="col-sm-4">
              <div class="col-sm-12 p-1">
                <label for="Pcolor">Colors</label>
                <select
                  #Pcolor
                  name="Pcolor"
                  id="Pcolor"
                  class="form-select"
                  aria-label="Default select example"
                  [(ngModel)]="ucolor"
                  formControlName="Pcolor"
                >
                  <option>Select Color</option>
                  <option *ngFor="let color of Colors">
                    {{ color }}
                  </option>
                </select>
                <div
                  class="form-control-feedback"
                  *ngIf="
                    validFrm.get('Pcolor')?.errors &&
                    (validFrm.get('Pcolor')?.dirty ||
                      validFrm.get('Pcolor')?.touched)
                  "
                >
                  <p
                    *ngIf="validFrm?.get('Pcolor')?.errors?.['required']"
                    class="text-danger"
                  >
                    this field is required
                  </p>
                </div>
              </div>
              <div class="col-sm-12 p-1">
                <label for="uSize">Sizes</label>
                <select
                  #uSize
                  name="uSize"
                  id="uSize"
                  class="form-select"
                  aria-label="Default select example"
                  [(ngModel)]="usize"
                  formControlName="uSize"
                >
                  <option>Select Size</option>
                  <option *ngFor="let size of Sizes">
                    {{ size }}
                  </option>
                </select>
                <div
                  class="form-control-feedback"
                  *ngIf="
                    validFrm.get('uSize')?.errors &&
                    (validFrm.get('uSize')?.dirty ||
                      validFrm.get('uSize')?.touched)
                  "
                >
                  <p
                    *ngIf="validFrm?.get('uSize')?.errors?.['required']"
                    class="text-danger"
                  >
                    this field is required
                  </p>
                </div>
              </div>

              <div class="col-sm-12 p-1">
                <label for="uQuantity">Quantities</label>
                <input
                  [(ngModel)]="uquantity"
                  #uQuantity
                  type="number"
                  class="form-control w-100"
                  id="uQuantity"
                  name="uQuantity"
                  formControlName="uQuantity"
                  min="1"
                />
                <div
                  class="form-control-feedback"
                  *ngIf="
                    validFrm.get('uQuantity')?.errors &&
                    (validFrm.get('uQuantity')?.dirty ||
                      validFrm.get('uQuantity')?.touched)
                  "
                >
                  <p
                    *ngIf="validFrm?.get('uQuantity')?.errors?.['required']"
                    class="text-danger"
                  >
                    this field is required
                  </p>
                </div>
                <div>
                  <button
                    class="button1 d-flex justify-content-center text-center m-auto mt-3"
                    type="button"
                    value="ADD Product Details"
                    (click)="uAddProductInfo()"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="20"
                      viewBox="0 0 20 20"
                      height="20"
                      fill="none"
                      class="svg-icon"
                    >
                      <g
                        stroke-width="1.5"
                        stroke-linecap="round"
                        stroke="#de8a2a"
                      >
                        <circle r="7.5" cy="10" cx="10"></circle>
                        <path d="m9.99998 7.5v5"></path>
                        <path d="m7.5 9.99998h5"></path>
                      </g>
                    </svg>
                    <span class="lable">Add</span>
                  </button>
                </div>
                <div
                  class="text-danger mt-2 d-flex justify-content-center"
                  *ngIf="duplicateError"
                >
                  <p>Color and Size Duplicated</p>
                </div>
                <div
                  class="text-danger mt-2 d-flex justify-content-center"
                  *ngIf="validFrm.invalid && validFrm.touched && !isSuccess"
                >
                  Please fill in all the required fields.
                </div>
              </div>
            </div>

            <div class="col-sm-4">
              <div>
                <table
                  class="table table-bordered container text-center mt-4 m-auto align-middle"
                >
                  <thead>
                    <tr>
                      <th>Color</th>
                      <th>Size</th>
                      <th>Quantity</th>
                      <th>Delete</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr *ngFor="let p of uproductInfo; let i = index">
                      <td>{{ p.color }}</td>
                      <td>{{ p.size }}</td>
                      <td>{{ p.quantity }}</td>
                      <td>
                        <button class="btn">
                          <svg
                            viewBox="0 0 15 17.5"
                            height="17.5"
                            width="15"
                            xmlns="http://www.w3.org/2000/svg"
                            class="icon"
                            (click)="udeleteProductInfo(i)"
                          >
                            <path
                              transform="translate(-2.5 -1.25)"
                              d="M15,18.75H5A1.251,1.251,0,0,1,3.75,17.5V5H2.5V3.75h15V5H16.25V17.5A1.251,1.251,0,0,1,15,18.75ZM5,5V17.5H15V5Zm7.5,10H11.25V7.5H12.5V15ZM8.75,15H7.5V7.5H8.75V15ZM12.5,2.5h-5V1.25h5V2.5Z"
                              id="Fill"
                            ></path>
                          </svg>
                        </button>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>

          <div class="d-flex justify-content-center mt-3 text-center m-auto">
            <button type="submit" class="button">
              <span class="button__text">Update</span>
              <span class="button__icon"
                ><svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  viewBox="0 0 24 24"
                  stroke-width="2"
                  stroke-linejoin="round"
                  stroke-linecap="round"
                  stroke="currentColor"
                  height="24"
                  fill="none"
                  class="svg"
                >
                  <line y2="19" y1="5" x2="12" x1="12"></line>
                  <line y2="12" y1="12" x2="19" x1="5"></line></svg
              ></span>
            </button>
          </div>
          <div
            class="text-danger mt-2 d-flex justify-content-center"
            *ngIf="validFrm.invalid && validFrm.touched && !isSuccess"
          >
            Please fill in all the required fields.
          </div>
        </div>
      </div>
    </form>
  </div>
</div>
