import { Component, OnInit } from '@angular/core';
import { OrderService } from '../../services/Orders/order.service';
import { OrderDto } from 'src/app/Models/Orders/OderDto';

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css'],
})
export class OrdersComponent implements OnInit {
  constructor(private readonly orderService: OrderService) { }

  AllOrders:OrderDto[]=[];
  ngOnInit(): void {
    this.orderService.getAllOrders().subscribe({
      next: (data: OrderDto[]) => {
        this.AllOrders = data;
        // console.log(this.paidOrders);
        // console.log(this.unpaidOrders);
      },
      error: (error) => console.log(error)
    });
  }
  changeToPaid(order: OrderDto): void {
    order.paymentStatus = 'Paid';
    order.orderStatus = "Delivered"
    this.orderService.updateOrder(order).subscribe({
      next: () => {
        //console.log("success")
      },
      error: (error) => console.log(error)
    });
  }
  get paidOrders(){
    return this.AllOrders.filter(order => order.paymentStatus === 'Paid');
  }
  get unpaidOrders(){
    return this.AllOrders.filter(order => order.paymentStatus === 'Unpaid') ;
  }
}





<div class="col-md-8 mx-auto mt-2">
  <div class="card">
    <div class="card-header">
      <h4 class="card-title text-center">All Unpaid Orders</h4>
    </div>
    <div class="card-body">
      <div class="table-responsive">
        <table class="table table-hover">
          <thead class=" text-primary">
            <th>
              Order Data
            </th>
            <th>
              Payment Method
            </th>
            <th>
              Order Status
            </th>
            <th>
              Customer Name
            </th>
            <th>
              Change to paid
            </th>
          </thead>
          <tbody>
            <tr *ngFor="let order of  unpaidOrders">
              <td>
                {{order.orderData}}
              </td>
              <td>
                {{order.paymentMethod}}
              </td>
              <td>
                {{order.orderStatus}}
              </td>
              <td>
                {{order.customerName}}
              </td>
              <td>
                <button class="btn btn-primary" (click)="changeToPaid(order)">Paid</button>


              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
</div>

<!-- ==================================================================== -->

<div class="col-md-8 mx-auto mt-2">
  <div class="card">
    <div class="card-header">
      <h4 class="card-title text-center">All Paid Orders</h4>
    </div>
    <div class="card-body">
      <div class="table-responsive">
        <table class="table table-hover">
          <thead class=" text-primary">
            <th>
              Order Data
            </th>
            <th>
              Payment Method
            </th>
            <th>
              Order Status
            </th>
            <th>
              Customer Name
            </th>
          </thead>
          <tbody>
            <tr *ngFor="let order of paidOrders">
              <td>
                {{order.orderData}}
              </td>
              <td>
                {{order.paymentMethod}}
              </td>
              <td>
                {{order.orderStatus}}
              </td>
              <td>
                {{order.customerName}}
              </td>

            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>
</div>
