import { Component, OnInit } from '@angular/core';
import { CategoriesService } from 'src/app/services/Category/categories.service';

@Component({
  selector: 'app-category-list',
  templateUrl: './category-list.component.html',
  styleUrls: ['./category-list.component.css'],
})
export class CategoryListComponent implements OnInit {
  constructor(private CategoryService: CategoriesService) {}
  ngOnInit(): void {
    this.getAllCatergories();
  }
  AllCategories: any[] | any = [];
  AllSubCategories: any[] | any = [];
  ParentCategories: any[] = [];
  getAllCatergories() {
    this.CategoryService.getAllCategories().subscribe((res) => {
      this.AllCategories = res;
      console.log(this.AllCategories);
      for (var i = 0; i < this.AllCategories.length; i++) {
        // console.log('loop');

        if (this.AllCategories[i].parentCategoryId != null) {
          this.AllSubCategories.push(this.AllCategories[i]);
        } else {
          this.ParentCategories.push(this.AllCategories[i]);
        }
      }
      console.log(this.AllSubCategories);
      console.log(this.ParentCategories);
    });
  }
  getRowCount(parentCategory: any): number {
    return this.AllSubCategories.filter(
      (subCategory: any) => subCategory.parentCategoryId === parentCategory.id
    ).length;
  }
}
<div class="col-md-12 container w-50 text-center border m-auto mt-4 mb-4">
  <div class="card-header m-3 p-3 border">
    <h4 class="card-title text-center">All Categories</h4>
  </div>
  <div class="card-body container table text-center">
    <div class="table-responsive text-center">
      <table class="table table-hover text-center">
        <thead class="text-primary">
          <tr>
            <th>Parent Categories</th>
            <th>Subcategories</th>
          </tr>
        </thead>
        <tbody>
          <ng-container *ngFor="let parentCategory of ParentCategories">
            <tr>
              <td>{{ parentCategory.name }}</td>
              <td>
                <ul class="subcategory-list">
                  <li
                    class="subcategory-item"
                    *ngFor="let subCategory of AllSubCategories"
                    [hidden]="
                      subCategory.parentCategoryId !== parentCategory.id
                    "
                  >
                    {{ subCategory.name }}
                  </li>
                </ul>
              </td>
            </tr>
          </ng-container>
        </tbody>
      </table>
    </div>
  </div>
</div>
